#!/usr/bin/env pwsh

$scriptDir = Split-Path -Parent $MyInvocation.MyCommand.Definition
$nodeExe = if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) { ".exe" } else { "" }
$cliPath = Join-Path $scriptDir "../html-minifier-terser/cli.js"

if (Test-Path (Join-Path $scriptDir "node$nodeExe")) {
    & (Join-Path $scriptDir "node$nodeExe") $cliPath @args
} else {
    & "node$nodeExe" $cliPath @args
}

exit $LASTEXITCODE
