#!/usr/bin/env pwsh

$scriptDir = Split-Path $MyInvocation.MyCommand.Definition -Parent
$nodeExe = if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) { ".exe" } else { "" }
$cliPath = Join-Path $scriptDir "../is-docker/cli.js"

if ($MyInvocation.ExpectingInput) {
    $input | & "$scriptDir/node$nodeExe" $cliPath @args
} else {
    & "$scriptDir/node$nodeExe" $cliPath @args
}

exit $LASTEXITCODE
