#!/usr/bin/env pwsh

$basedir = Split-Path $MyInvocation.MyCommand.Definition -Parent
$exe = if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) { ".exe" } else { "" }
$args = @()
if ($MyInvocation.UnboundArguments) { $args += $MyInvocation.UnboundArguments }
$args += "$basedir/../react-scripts/bin/react-scripts.js"

& "$basedir/node$exe" @args
exit $LASTEXITCODE
