import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import { Container, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { getAllCategory, deleteCategories as deleteCategoriesAction, addCategory, updateCategories } from '../../actions';
import Input from '../../components/UI/Input';
import Modal from '../../components/UI/Modal';
import CheckboxTree from 'react-checkbox-tree';
import { IoCheckboxOutline, IoCheckbox, IoChevronForward, IoChevronDown } from "react-icons/io5";
import 'react-checkbox-tree/lib/react-checkbox-tree.css';
import UpdateCategoriesModal from './components/UpdateCategoriesModal';
import AddCategoryModal from './components/AddCategoryModal';






/**
 * @autheur
 * @fonction Category
 **/

const Category = (props) => {
    const category = useSelector(state => state.category);
    const [categoryName, setCategoryName] = useState('');
    const [parentCategoryId, setParentCategoryId] = useState('');
    const [categoryImage, setCategoryImage] = useState('');
    const [show, setShow] = useState(false);
    const [checked, setChecked] = useState([]);
    const [expanded, setExpanded] = useState([]);
    const [checkedArray, setCheckedArray] = useState([]);
    const [expandedArray, setExpandedArray] = useState([]);
    const [updateCategoryModal, setUpdateCategoryModal] = useState(false);
    const [deleteCategoryModal, setDeleteCategoryModal] = useState(false);
    const dispatch = useDispatch();





    const handleClose = () => {

        const form = new FormData();


        form.append('name', categoryName);
        form.append('parentId', parentCategoryId);
        form.append('categoryImage', categoryImage);
        dispatch(addCategory(form));
        setCategoryName('');
        setParentCategoryId('');


        // const cat = {
        //  categoryName,
        //  parentCategoryId,
        //   categoryImage
        //};



        setShow(false);
    }
    const handleShow = () => setShow(true);

    const renderCategories = (categories) => {
        let myCategories = [];
        for (let category of categories) {
            myCategories.push(
                {
                    label: category.name,
                    value: category._id,
                    children: category.children.length > 0 && renderCategories(category.children)
                }
            )

        }
        return myCategories;
    }

    const createCategoryList = (categories, options = []) => {

        for (let category of categories) {
            options.push({ value: category._id, name: category.name, parentId: category.parentId });
            if (category.children.length > 0) {
                createCategoryList(category.children, options)
            }
        }
        return options;
    }




    const handleCategoryImage = (e) => {
        setCategoryImage(e.target.files[0]);
    }

    const updateCategory = () => {
        updateCheckedAndExpandedCategories();
        setUpdateCategoryModal(true);
    }

    const updateCheckedAndExpandedCategories = () => {
        const categories = createCategoryList(category.categories);
        const checkedArray = [];
        const expandedArray = [];
        checked.length > 0 && checked.forEach((categoryId, index) => {
            const category = categories.find((category, _index) => categoryId == category.value)
            category && checkedArray.push(category);
        })
        expanded.length > 0 && expanded.forEach((categoryId, index) => {
            const category = categories.find((category, _index) => categoryId == category.value)
            category && expandedArray.push(category);
        })
        setCheckedArray(checkedArray);
        setExpandedArray(expandedArray);
    }

    const handleCategoryInput = (key, value, index, type) => {
        if (type == "checked") {
            const updatedCheckedArray = checkedArray.map((item, _index) => index == _index ? { ...item, [key]: value } : item);
            setCheckedArray(updatedCheckedArray);
        } else if (type == "expanded") {
            const updatedExpandedArray = expandedArray.map((item, _index) => index == _index ? { ...item, [key]: value } : item);
            setExpandedArray(updatedExpandedArray);
        }
    }

    const updateCategoriesForm = () => {
        const form = new FormData();

        expandedArray.forEach((item, index) => {
            form.append('_id', item.value);
            form.append('name', item.name);
            form.append('parentId', item.parentId ? item.parentId : "");
            form.append('type', item.type);
        });
        checkedArray.forEach((item, index) => {
            form.append('_id', item.value);
            form.append('name', item.name);
            form.append('parentId', item.parentId ? item.parentId : "");
            form.append('type', item.type);
        });

        dispatch(updateCategories(form))
            .then(result => {
                if (result) {
                    dispatch(getAllCategory())
                    setUpdateCategoryModal(false);
                }
            })
    }


   

    const deleteCategory = () => {
        updateCheckedAndExpandedCategories();
        setDeleteCategoryModal(true);

    }
    const deleteCategories = () => {
        const checkedIdsArray = checkedArray.map((item, index) => ({ _id: item.value }));
        const expandedIdsArray = expandedArray.map((item, index) => ({ _id: item.value }));
        const idsArray = expandedIdsArray.concat(checkedIdsArray);

        if (checkedIdsArray.length > 0) {
            dispatch(deleteCategoriesAction(checkedIdsArray))
                .then(result => {
                    if (result) {
                        dispatch(getAllCategory());
                        setDeleteCategoryModal(false);
                    }
                }
                );
        }

    }
    const renderDeleteCategoryModal = () => {
        return (
            <Modal
                modalTitle="Confirmer"
                show={deleteCategoryModal}
                handleClose={() => setDeleteCategoryModal(false)}
                buttons={[
                    {
                        label: 'Non',
                        color: 'primary',
                        onClick: () => {
                            alert('non');
                        }
                    },
                    {
                        label: 'Oui',
                        color: 'danger',
                        onClick: deleteCategories
                    }
                ]}
            >
                <h5>Deroulé</h5>
                {expandedArray.map((item, index) => <span key={index}>{item.name}</span>)}
                <h5>Vérifié</h5>
                {checkedArray.map((item, index) => <span key={index}>{item.name}</span>)}

            </Modal>
        );
    }

    return (
        <Layout sidebar>
            <Container>
                <Row >
                    <Col md={12}>
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <h3>Categorie</h3>
                            <button onClick={handleShow}>Ajouter</button>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <Col md={12}>
                        {/*<ul>
                            {renderCategories(category.categories)}

                        </ul>*/}
                        <CheckboxTree
                            nodes={renderCategories(category.categories)}
                            checked={checked}
                            expanded={expanded}
                            onCheck={checked => setChecked(checked)}
                            onExpand={expanded => setExpanded(expanded)}
                            icons={{
                                check: <IoCheckbox />,
                                uncheck: <IoCheckboxOutline />,
                                halfCheck: <IoCheckboxOutline />,
                                expandClose: <IoChevronForward />,
                                expandOpen: <IoChevronDown />
                            }}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <button onClick={deleteCategory}>Supprimer</button>
                        <button onClick={updateCategory}>Modifier</button>
                    </Col>
                </Row>
            </Container>
            <AddCategoryModal
            show={updateCategoryModal}
            handleClose={updateCategoriesForm}
            modalTitle={'Mise à Jour des Categories'}
            size="lg"
            expandedArray={expandedArray}
            checkedArray={checkedArray}
            handleCategoryInput={handleCategoryInput}
            categoryList={createCategoryList(category.categories)}
            />
            <UpdateCategoriesModal 
              show={updateCategoryModal}
              handleClose={updateCategoriesForm}
              modalTitle={'Mise à Jour des Categories'}
              size="lg"
              expandedArray={expandedArray}
              checkedArray={checkedArray}
              handleCategoryInput={handleCategoryInput}
              categoryList={createCategoryList(category.categories)}
            />
            {renderDeleteCategoryModal()}

        </Layout>

    )
}

export default Category
