import React, { useState } from 'react';
import Layout from '../../components/Layout';
import { Container, Row, Col, Table } from 'react-bootstrap';
import Input from '../../components/UI/Input';
import Modal from '../../components/UI/Modal';
import { useDispatch, useSelector } from 'react-redux';
import { addProduct, deleteProductById } from '../../actions';
import './style.css';
import { generatePublicUrl } from '../../urlConfig';



/**
* @author
* @function Products
**/

export const Products = (props) => {

  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [productPictures, setProductPictures] = useState('');
  const [show, setShow] = useState(false);
  const [productDetailModal, setProductDetailModal] = useState(false);
  const [productDetails, setProductDetails] = useState(null);
  const category = useSelector(state => state.category);
  const product = useSelector(state => state.product);
  const dispatch = useDispatch();



  const handleClose = () => {
    setShow(false);
  }
  const submitProductForm = () => {
    const form = new FormData();
    form.append('name', name);
    form.append('quantity', quantity);
    form.append('price', price);
    form.append('description', description);
    form.append('category', categoryId);

    for (let pic of productPictures) {
      form.append('productPicture', pic);
    }

    dispatch(addProduct(form));
  }
  
  const handleShow = () => setShow(true);


  const createCategoryList = (categories, options = []) => {

    for (let category of categories) {
      options.push({ value: category._id, name: category.name });
      if (category.children.length > 0) {
        createCategoryList(category.children, options)
      }
    }
    return options;
  }



  const handleProductPictures = (e) => {
    setProductPictures([
      ...productPictures,
      e.target.files[0]
    ]);
  }

  const renderProducts = () => {
    return (
      <Table style={{ fontSize: 12 }} responsive="sm">
        <thead>
          <tr>
            <th>#</th>
            <th>Nom</th>
            <th>Prix</th>
            <th>Quantité</th>
            <th>Catégorie</th>
          </tr>
        </thead>
        <tbody>
          {
            product.products.length > 0 ?
              product.products.map((product) =>(
                <tr key={product._id}>
                  <td>2</td>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.quantity}</td>
                  <td>{product.category.name}</td>
                  <td>
                    <button onClick={() => showProductDetailsModal(product)}>détails</button>
                    <button onClick={() => {
                      const payload = {
                        productId: product._id,
                      }
                      dispatch(deleteProductById(payload));
                    
                    }}>supprimé</button>
                  </td>
                </tr>
              )) 
              : null}

        </tbody>
      </Table>
    );
  }

  const renderAddProductModal = () => {
    return (
      <Modal 
      show={show} 
      handleClose={handleClose}
        modalTitle={'Ajouter un nouveau Produit'} 
        onsubmit={submitProductForm}
        >
        <Input
          label="Name"
          value={name}
          placeholder={`Nom Produit`}
          onChange={(e) => setName(e.target.value)}
        />
        <Input
          label="Quantity"
          value={quantity}
          placeholder={`Quantité`}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <Input
          label="Price"
          value={price}
          placeholder={`Prix`}
          onChange={(e) => setPrice(e.target.value)}
        />
        <Input
          label="Description"
          value={description}
          placeholder={`description du produit`}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select className="form-control"
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}>
          <option>
            choisir la catégorie
          </option>
          {
            createCategoryList(category.categories).map(option =>
              <option key={option.value} value={option.value}>{option.name}</option>)
          }
        </select>



        {
          productPictures.length > 0 ?
            productPictures.map((pic, index) => <div key={index}>{pic.name}</div>) : null
        }

        <input type="file" name="productPicture" onChange={handleProductPictures} />
      </Modal>
    )
  }

  const handleCloseProductDetailsModal = () => {
    setProductDetailModal(false);
  }
  const showProductDetailsModal = (product) => {
    setProductDetails(product);
    setProductDetailModal(true);
  }
  const renderProductDetailsModal = () => {


    if (!productDetails) {
      return null;
    }


    return (
      <Modal
        show={productDetailModal}
        handleClose={handleCloseProductDetailsModal}
        modalTitle={'Détails du produit'}
        size="lg"
      >
        <Row>
          <Col md="6">
            <label className="key">Nom</label>
            <p className="value">{productDetails.name}</p>
          </Col>
          <Col md="6">
            <label className="key">Prix</label>
            <p className="value">{productDetails.price}</p>
          </Col>
          <Col md="6">
            <label className="key">Quantité</label>
            <p className="value">{productDetails.quantity}</p>
          </Col>
          <Col md="6">
            <label className="key">Catégorie</label>
            <p className="value">{productDetails.category.name}</p>
          </Col>
          <Col md="6">
            <label className="key">Description</label>
            <p className="value">{productDetails.description}</p>
          </Col>

        </Row>
        <Row>
          <Col>
            <label className="key">Images du Produit</label>
            <div style={{ display: 'flex' }}>
              {productDetails.productPictures.map(picture =>
                <div className="productImgContainer">
                  <img src={generatePublicUrl(picture.img)} />
                </div>

              )}
            </div>
          </Col>
        </Row>

      </Modal>
    )
  }

  return (
    <Layout sidebar>
      <Container>
        <Row >
          <Col md={12}>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <h3>Produits</h3>
              <button onClick={handleShow}>Ajouter</button>
            </div>
          </Col>
        </Row>
        <Row>
          <Col>
            {renderProducts()}
          </Col>
        </Row>
      </Container>

      {renderAddProductModal()}
      {renderProductDetailsModal()}

    </Layout>
  );

}

export default Products;
