import React, { useState } from 'react';
import Layout from '../../components/Layout';
import { useSelector, useDispatch } from 'react-redux';
import Card from '../../components/UI/Card';
import './style.css';
import { updateOrder } from '../../actions'; 

/**
* @author
* @function Orders
**/

const Orders = (props) => {
  const order = useSelector((state) => state.order);
  const [type, setType] = useState('');
  const dispatch = useDispatch();

  const onOrderUpdate = (orderId) => {
    const payload = {
      orderId,
      type,
    };
    dispatch(updateOrder(payload));
  }

  const formatDate = (date) => {
    if(date){
      const d = new Date(date);
      return `${d.getFullYear()}-${d.getMonth() + 1}-${d.getDate()}`;
    }
    return "";
  };

  return (
    <Layout sidebar>
    {
      order.orders.map((orderItem, index) => (
        <Card 
        style={{margin: "10px 0"}}
        key={index} 
        headerLeft={orderItem._id}>
        <div style={{
          display: "flex",
          justifyContent: "space-between",
          padding: "50px 50px",
          alignItems: 'center',
        }}>
        <div>
          <div className="title">Articles</div>
          {orderItem.items.map((item, index) => (
            <div className="value" key={index}>
              {item.productId.name}
            </div>
          ))}
        </div>
        <div>
          <span className="title">Montant Total</span><br />
          <span className='value'>{orderItem.totalAmount}</span>
        </div>
        <div>
          <span className="title">Type de Paiement</span><br />
          <span className='value'>{orderItem.paymentType}</span>
        </div>
        <div>
          <span className="title">Statut de Paiement</span><br />
          <span className='value'>{orderItem.paymentStatus}</span>
        </div>
        </div>
        <div style={{
          boxSizing: 'border-box',
          padding: '100px',
          display: 'flex',
          alignItems: 'center'
        }}>
          <div className='orderTrack'>
            <div className="orderStatus">
              <div className="point"></div>
              <div className="orderInfo">
                <div className="status">Commande passée</div>
                <div className='date'>Fri, 2024</div>
              </div>
            </div>
            <div className="orderStatus">
              <div className="point"></div>
              <div className="orderInfo">
                <div className="status">Emballé</div>
                <div className='date'>Fri, 2024</div>
              </div>
            </div>
            <div className="orderStatus">
              <div className="point"></div>
              <div className="orderInfo">
                <div className="status">Envoyé</div>
                <div className='date'>Fri, 2024</div>
              </div>
            </div>
            <div className="orderStatus">
              <div className="point"></div>
              <div className="orderInfo">
                <div className="status">Livraison</div>
                <div className='date'>Fri, 2024</div>
              </div>
            </div>
          </div>


          {/* seclect input to apply order action */}
          <div style={{
            padding: '0 50px',
            boxSizing: 'border-box',
          
          }}>
          <select onChange={(e) => setType(e.target.value)} >
          <option value={""}>select status</option>
            {orderItem.orderStatus.map((status) => {
              
              return (
              <>
              {
                !status.isCompleted ? (
                  <option key={status.type} value={status.type}>
                {status.type}</option>
                ) : null
              }
                
              </>
              );
            })}
          </select>
          </div>
          {/* button to confirm action */}
          <div style={{
            padding: '0 50px',
            boxSizing: 'border-box',
          
          }}>
            <button onClick={() => onOrderUpdate(orderItem._id)}>Confirmer</button>
          </div>
        </div>
      </Card>
      ))
    }
     
    </Layout>
  );

}


export default Orders;
