import axios from "../helpers/axios";
import { orderConstantsConstants } from "./constants";


export const getCustomerOrders = () => {
    return async dispatch => {
        dispatch({ type: orderConstantsConstants.GET_CUSTOMER_ORDER_REQUEST });
        try {
            const res = await axios.post('/order/getCustomerOrders');
            console.log(res);
            if (res.status === 200) {
                const { orders } = res.data;
                dispatch({
                    type: orderConstantsConstants.GET_CUSTOMER_ORDER_SUCCESS,
                    payload: { orders }
                });
            } else {
                dispatch({
                    type: orderConstantsConstants.GET_CUSTOMER_ORDER_FAILURE,
                    payload: { error: res.data.error }
                });
            }
        } catch (error) {
            console.log(error)
        }
    }
}

