#!/usr/bin/env pwsh

$scriptDir = Split-Path $MyInvocation.MyCommand.Definition -Parent
$exe = if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) { ".exe" } else { "" }
$semverPath = Join-Path $scriptDir "../semver/bin/semver$exe"

if (-not (Test-Path $semverPath)) {
    Write-Error "semver.js not found at $semverPath"
    exit 1
}

& $semverPath $args
exit $LASTEXITCODE
