"use strict";

const { iterableToArray } = require("./iterableToArray");

function iterableToArrayLimitLoose(iterable, maxLength = Number.POSITIVE_INFINITY) {
  // Use the iterableToArray function to get an array from the iterable
  const array = iterableToArray(iterable);

  // Return the first `maxLength` elements of the array
  return array.slice(0, maxLength);
}

module.exports = iterableToArrayLimitLoose;
