"use strict";

const helperPluginUtils = require("@babel/helper-plugin-utils");
const createClassFeaturePlugin = require("@babel/helper-create-class-features-plugin");

const defaultPlugin = (api, options) => {
  api.assertVersion(7);

  return createClassFeaturePlugin({
    name: "proposal-class-properties",
    api,
    feature: createClassFeaturePlugin.FEATURES.fields,
    loose: options.loose,

    manipulateOptions(opts, parserOpts) {
      parserOpts.plugins.push("classProperties", "classPrivateProperties");
    }
  });
};

module.exports = helperPluginUtils.declare(defaultPlugin);
