"use strict";

const { declare } = require("@babel/helper-plugin-utils");
const { addOptionalChainingPlugin } = require("./helper-plugin-utils");

const defaultPlugin = declare((api) => {
  api.assertVersion(7);

  return {
    name: "syntax-optional-chaining",
    manipulateOptions(opts, parserOpts) {
      addOptionalChainingPlugin(parserOpts);
    },
  };
});

module.exports = defaultPlugin;
