"use strict";

const { declare } = require("@babel/helper-plugin-utils");
const privateInPlugin = require("@babel/plugin-syntax-private-property-in-object");

module.exports = declare((api) => {
  api.assertVersion(7);

  return {
    name: "syntax-private-property-in-object",

    manipulateOptions(opts, parserOpts) {
      if (!parserOpts.plugins) parserOpts.plugins = [];
      parserOpts.plugins.push(privateInPlugin);
    },
  };
});

