const defineAccessor = (obj, propName, getter, setter, config = {}) => {
  const finalConfig = {
    configurable: true,
    enumerable: true,
    ...config
  };

  if (getter) {
    Object.defineProperty(obj, propName, {
      ...finalConfig,
      get: getter
    });
  } else if (setter) {
    Object.defineProperty(obj, propName, {
      ...finalConfig,
      set: setter
    });
  } else {
    Object.defineProperty(obj, propName, finalConfig);
  }
};

// Usage example
const myObj = {};
defineAccessor(myObj, 'foo', () => 42, value => value + 1, { writable: false });
console.log(myObj.foo); // 42
myObj.foo = 10;
console.log(myObj.foo); // 43
