// Use a more descriptive function name
export default function setPrototypeOf(o, p) {
  // Check if Object.setPrototypeOf is supported
  if (Object.setPrototypeOf) {
    return Object.setPrototypeOf(o, p);
  } else {
    // If not supported, use the __proto__ property
    o.__proto__ = p;
    return o;
  }
}
