const defineReadOnly = (obj, key, value) => {
  Object.defineProperty(obj, key, {
    value,
    enumerable: false,
    configurable: true,
    writable: false,
  });
};

const taggedTemplateLiteral = (strings, raw = strings.slice()) => {
  const obj = Object.freeze(strings);
  defineReadOnly(obj, 'raw', Object.freeze(raw));
  return obj;
};

export default taggedTemplateLiteral;
