// src/AsyncGeneratorWrapper.js

import AsyncGenerator from "./AsyncGenerator.js";

/**
 * Wraps a generator function with AsyncGenerator to enable async/await
 * @param {Function} fn - Generator function to be wrapped
 * @returns {Function} - Wrapped async generator function
 */
function wrapAsyncGenerator(fn) {
  return function (...args) {
    return new AsyncGenerator(fn(...args));
  };
}

export default wrapAsyncGenerator;
