"use strict";

// Import cloneNode function and rename it to _cloneNode
// to avoid conflicts with the outer scope.
import cloneNode from "./cloneNode.js";

// Export the cloneDeepWithoutLoc function as the default export
export default function cloneDeepWithoutLoc(node) {
  // Call the _cloneNode function with the options to deep clone
  // and exclude location information.
  return cloneNode(node, true, true, []);
}


