"use strict";

import isFunctionDeclaration from "./generated/isFunctionDeclaration.js";
import isClassDeclaration from "./generated/isClassDeclaration.js";
import isLet from "./isLet.js";

/**
 * Checks if a node is block-scoped.
 * @param {Object} node - The node to check.
 * @returns {boolean} True if the node is block-scoped, false otherwise.
 */
function isBlockScoped(node) {
  return (
    isFunctionDeclaration(node) ||
    isClassDeclaration(node) ||
    isLet(node)
  );
}

export default isBlockScoped;
