/** The name of the invalid layer placeholder. This allows us to ignore this rule in further processing. */
export const INVALID_LAYER_NAME = "csstools-invalid-layer";

/** The name of the layer placeholder used for any layer at rules that contain selectors. This allows us to sort these differently from other layer at rules. */
export const WITH_SELECTORS_LAYER_NAME = "csstools-layer-with-selector-rules";

/** The suffix for anonymous layers. */
export const ANONYMOUS_LAYER_SUFFIX = "6efdb677-bb05-44e5-840f-29d2175862fd";

/** The suffix for implicit layers. */
export const IMPLICIT_LAYER_SUFFIX = "b147acf6-11a6-4338-a4d0-80aef4cd1a2f";

/** A list of conditional at-rules. */
export const CONDITIONAL_ATRULES: string[] = [];

/** A list of at-rules with non-selector block lists. */
export const ATRULES_WITH_NON_SELECTOR_BLOCK_LISTS: string[] = [];
