type Color = [number, number, number];

export function sRgbToSRgb(sRgb: Color): Color {
  const [r, g, b] = sRgb;

  // Apply the sRGB to linear RGB transformation
  const linearR = r <= 0.04045 ? r / 12.92 : Math.pow((r + 0.055) / 1.055, 2.4);
  const linearG = g <= 0.04045 ? g / 12.92 : Math.pow((g + 0.055) / 1.055, 2.4);
  const linearB = b <= 0.04045 ? b / 12.92 : Math.pow((b + 0.055) / 1.055, 2.4);

  // Apply the linear RGB to sRGB transformation
  const sRgbR = linearR <= 0.0031308 ? 12.92 * linearR : 1.055 * Math.pow(linearR, 1 / 2.4) - 0.055;
  const sRgbG = linearG <= 0.0031308 ? 12.92 * linearG : 1.055 * Math.pow(linearG, 1 / 2.4) - 0.055;
  const sRgbB = linearB <= 0.0031308 ? 12.92 * linearB : 1.055 * Math.pow(linearB, 1 / 2.4) - 0.055;

  return [sRgbR, sRgbG, sRgbB];
}

export {};
