import type { Node as PostcssNode } from 'postcss';

export declare function hasSupportsAtRuleAncestor(node: PostcssNode): boolean {
  let currentNode: PostcssNode | null = node.parent;

  while (currentNode !== null) {
    if (currentNode.type === 'atrule' && currentNode.name === 'supports') {
      return true;
    }
    currentNode = currentNode.parent;
  }

  return false;
}
