export function numberOfCalcOccurrences(value: string): number {
  // Regular expression to match the word 'calc' in a case-insensitive manner
  const calcRegEx = /calc/i;

  // Check if the word 'calc' is present in the string and return the number of occurrences
  return value.match(calcRegEx) ? value.match(calcRegEx).length : 0;
}
