import { DelimiterCase } from './delimiter-case';

/**
 * Convert a string literal to snake_case.
 *
 * This can be useful when, for example, converting a camelCased object property to a snake_cased SQL column name.
 *
 * @example
 * ```typescript
 * import { SnakeCase } from 'type-fest';
 *
 * // Simple
 * const someVariable: SnakeCase<'fooBar'> = 'foo_bar';
 *
 * // Advanced
 * type SnakeCasedProps<T> = {
 * 	[K in keyof T as SnakeCase<string & K>]: T[K];
 * };
 *
 * interface ModelProps {
 * 	isHappy: boolean;
 * 	fullFamilyName: string;
 * 	foo: number;
 * }
 *
 * const dbResult: SnakeCasedProps<ModelProps> = {
 * 	'is_happy': true,
 * 	'full_family_name': 'Carla Smith',
 * 	foo: 123,
 * };
 * ```
 */
export type SnakeCase<Value extends string> = DelimiterCase<Value, '_'>;
