/**
 * Gets the Jest configuration objects for the specified projects.
 *
 * @param namesOfProjectsToRun - The names of the projects to retrieve the configurations for.
 * @param projectConfigs - The Jest configuration objects for all projects.
 * @returns The Jest configuration objects for the specified projects.
 */
export default function getConfigsOfSpecifiedProjects(
  namesOfProjectsToRun: string[],
  projectConfigs: Config.ProjectConfig[]
): Config.ProjectConfig[] {
  return namesOfProjectsToRun.flatMap((name) => {
    const projectConfig = projectConfigs.find((config) => config.name === name);
    return projectConfig ? [projectConfig] : [];
  });
}
