import type { Config } from '@jest/types';
import type { TestRunData } from './types';

/**
 * Returns a string indicating that no tests were found.
 * @param testRunData - The data about the current test run.
 * @param globalConfig - The global configuration for Jest.
 * @returns A message indicating that no tests were found.
 */
export default function getNoTestFound(testRunData: TestRunData, globalConfig: Config.GlobalConfig): string {
  return 'No tests found';
}
