import type { GlobalConfig } from '@jest/types';

/**
 * Returns an error message if no test file is found, otherwise returns an empty string.
 * @param globalConfig - The global configuration object for Jest.
 * @returns An error message or an empty string.
 */
export default function getNoTestFoundFailed(globalConfig: GlobalConfig): string {
  if (globalConfig.testMatch.length === 0) {
    return 'No test files found. Please configure the "testMatch" or "testPathPattern" property in your Jest configuration.';
  }
  return '';
}

