import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Layout from '../../components/Layout';
import Card from '../../components/UI/Card';
import { getAddress } from '../../actions';
import { MaterialButton } from '../../components/MaterialUI';
import AddressForm from './AddressForm';
import './style.css';

/**
* @author
* @function CheckoutPage
**/



const CheckoutStep = (props) => {
  return (
    <div className="checkoutStep">
      <div onClick={props.onCheckoutStep} className={`checkoutHeader ${props.active && 'active'}`}>
        <div>
          <span className="stepNumber">{props.stepNumber}</span>
          <span className="stepTitle">{props.title}</span>
        </div>
      </div>
      {props.body && props.body}
    </div>
  );
}


export const CheckoutPage = (props) => {

    const user = useSelector(state => state.user);
    const dispatch = useDispatch();

    const onAddressSubmit = () => {

    }

    useEffect(() => {
      dispatch(getAddress());
    }, []);



  return(
    <Layout>
    <div className="cartContainer" style={{ alignItems: 'flex-start' }}>
        <div className="checkoutContainer">
            <CheckoutStep
              stepNumber={'1'}
              title={'LOGIN'}
              active={!user.auth.authenticate}
              body={<div className="loginForm">
                <form>
                  <input name="email" type="text" />
                  <MaterialButton title="LOGIN" bgColor="#fb641b" textColor="#ffffff" />
                </form>
              </div>}
            />
            <CheckoutStep
              stepNumber={'2'}
              title={'DELIVERY ADDRESS'}
              active
              body={<AddressForm onSubmitForm={onAddressSubmit} />}
            />
            <CheckoutStep
              stepNumber={'3'}
              title={'ORDER SUMMARY'}
              active
              body={<AddressForm onSubmitForm={onAddressSubmit} />}
            />
            <CheckoutStep
              stepNumber={'4'}
              title={'PAYMENT OPTIONS'}
              active
              body={<AddressForm onSubmitForm={onAddressSubmit} />}
            />
        </div>
    </div>
    </Layout>
   )

 }

 export default CheckoutPage;
