import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addAddress } from "../../actions";
import { MaterialButton, MaterialInput } from "../../components/MaterialUI";

/**
 * @author
 * @function AddressForm
 **/


const AddressForm = (props) => {

    const [name, setName] = useState('');
    const [mobileNumber, setMobileNumber] = useState('');
    const [locality, setLocality] = useState('');
    const [address, setAddress] = useState('');
    const [cityDistrictTown, setCityDistrictTown] = useState('');
    const [state, setState] = useState('');
    const [landmark, setLandmark] = useState('');
    const [alternatePhone, setAlternatePhone] = useState('');
    const [addressType, setAddressType] = useState('');
    const dispatch = useDispatch();
    const user = useSelector((state) => state.user);
    const [submitFlag, setSubmitFlag] = useState(false);

    const inputContainer = {
        width: "100%",
        marginRight: 10
    };

    const onAddressSubmit = (e) => {
        const payload = {
            address: {
                name,
                mobileNumber,
                locality,
                address,
                cityDistrictTown,
                state,
                landmark,
                alternatePhone,
                addressType,
            },
        };
        console.log(payload);
        dispatch(addAddress(payload));
        setSubmitFlag(true);
    }
    const { onSubmitForm } = props;

    useEffect(() => {
        console.log("addressCount", user.address);
        if(submitFlag){
            console.log("où sommes-nous", user);
            const address = user.address.slice(user.address.length - 1)[0];
            onSubmitForm(address);
        }
    }, [user.address, user, onSubmitForm, submitFlag]);
    
    const renderAddressForm = () => {
        return (
            <>
                <div className="flexRow">
                    <div style={inputContainer}>
                        <MaterialInput
                            label="Nom"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>
                    <div style={inputContainer}>
                        <MaterialInput
                            label="10-digit numero telephone"
                            value={mobileNumber}
                            onChange={(e) => setMobileNumber(e.target.value)}
                        />
                    </div>
                </div>
                <div className="flexRow">
                    <div style={inputContainer}>
                        <MaterialInput
                            label="Commune"
                            value={locality}
                            onChange={(e) => setLocality(e.target.value)}
                        />
                    </div>
                    <div style={inputContainer}>
                        <MaterialInput
                            label="Quartier"
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}
                        />
                    </div>
                </div>
                <div className="flexRow">
                    <div style={inputContainer}>
                        <MaterialInput
                            label="Sous-quartier"
                            value={cityDistrictTown}
                            onChange={(e) => setCityDistrictTown(e.target.value)}
                        />
                    </div>
                    <div style={inputContainer}>
                        <MaterialInput
                            label="Pays"
                            value={state}
                            onChange={(e) => setState(e.target.value)}
                        />
                    </div>
                </div>
                <div className="flexRow">
                    <div style={inputContainer}>
                        <MaterialInput
                            label="Point de repere"
                            value={landmark}
                            onChange={(e) => setLandmark(e.target.value)}
                        />
                    </div>
                    <div style={inputContainer}>
                        <MaterialInput
                            label="Numero de telephone alternatif (Optionnel)"
                            value={alternatePhone}
                            onChange={(e) => setAlternatePhone(e.target.value)}
                        />
                    </div>
                    </div>
                    <div>
                        <label>Adresse Type</label>
                        <div className="flexRow">
                        <div>
                            <input 
                            type="radio" 
                            onClick={() => setAddressType('home')} name='addressType' 
                            value='home' />
                            <span>Domicile</span>
                            <input type="radio" onClick={() => setAddressType('work')} name='addressType' value='work' />
                            <span>Travail</span>
                        </div>
                    </div>
                </div>
                <div className="flexRow">
                    <MaterialButton
                        title="ENREGISTRER ET LIVRER ICI"
                        onClick={onAddressSubmit}
                        style={{
                            width: '250px',
                            margin: '20px 0'
                        }}
                    />
                </div>
            </>
        )
    }

    if (props.withoutLayout) {
        return <div>{renderAddressForm()}</div>
    }

    return (

        <div className="checkoutStep" style={{ background: '#f5faff' }}>
            <div className={`checkoutHeader`}>
                <div>
                    <span className="stepNumber">+</span>
                    <span className="stepTitle">AJOUTER NOUVELLE ADDRESSE</span>
                </div>
            </div>
            <div style={{
                padding: '0 60px',
                paddingBottom: '20px',
                boxSizing: 'border-box'
            }}>
                {renderAddressForm()}
            </div>
        </div>

    );
}

export default AddressForm;
